package com.example.praiademanoelviana.activity.model;import com.example.praiademanoelviana.activity.Helper.ConfiruFirebase;import com.google.firebase.database.DatabaseReference;public class Usuario {    private  String idUsuario;    private String nome;    private  String endereco;    private String cpf;    private String urlImagemUsuario;    public Usuario() {    }    public String getIdUsuario() {        return idUsuario;    }public  void salvar(){    DatabaseReference firebaseRef = ConfiruFirebase.getFirebase();    DatabaseReference usuarioRef = firebaseRef            .child("usuarios")            .child(getIdUsuario());    usuarioRef.setValue(this);    }    public String getUrlImagemUsuario() {        return urlImagemUsuario;    }    public void setUrlImagemUsuario(String urlImagemUsuario) {        this.urlImagemUsuario = urlImagemUsuario;    }    public void setIdUsuario(String idUsuario) {        this.idUsuario = idUsuario;    }    public String getNome() {        return nome;    }    public void setNome(String nome) {        this.nome = nome;    }    public String getEndereco() {        return endereco;    }    public void setEndereco(String endereco) {        this.endereco = endereco;    }    public String getCpf() {        return cpf;    }    public void setCpf(String cpf) {        this.cpf = cpf;    }}